package geometry;/** Represents any transformation from a Vector2D to another Vector2D*/public abstract class Transformation{  /**  Transforms the specified vector  */  public abstract Vector2D transform(Vector2D v);    /**   Does this transformation actually change any vectors when they pass through it?  */  public abstract boolean isIdentity();    /**   Gets the inverse of this transformation.      For any transformation t and vector v, t.getInverse().transform(t.transform(v)) should return a clone of v.  */  public abstract Transformation getInverse();}