package gui;import geometry.*;import mapping.ObstacleMap;import javax.swing.*;import java.awt.event.*; // ActionEvent, ActionListenerimport java.awt.*; // GridLayoutpublic class TestControlPanel extends JPanel{ private JButton resetMapButton; private JButton eStopButton; private JButton startAutonomousControlButton; private MapDisplayFrame mapframe; private GraphPanel speedGraph; private GraphPanel mergeScoreGraph; private GraphPanel matchRatioGraph; private GraphPanel rotationSpeedGraph;    public TestControlPanel(MapDisplayFrame mapFrame)  {     this.mapframe = mapFrame;	 speedGraph = new GraphPanel(250);	 mergeScoreGraph = new GraphPanel(250);	 rotationSpeedGraph = new GraphPanel(250);	 matchRatioGraph = new GraphPanel(250);	 rotationSpeedGraph.setTitle("Estimated Rotation Speed");	 speedGraph.setTitle("Estimated Speed");	 mergeScoreGraph.setTitle("Map Merge Score");	 matchRatioGraph.setTitle("Match Ratio");     resetMapButton = new JButton("Reset Map");	 eStopButton = new JButton("E-Stop");	 startAutonomousControlButton = new JButton("Start Auto-controls");     resetMapButton.addActionListener(	   new ActionListener()	   {	     public void actionPerformed(ActionEvent ae)		 {		   mapframe.clearMapAndStopAll();		 }	   }	  );	  	 eStopButton.addActionListener(	   new ActionListener()	   {	     public void actionPerformed(ActionEvent ae)		 {		   mapframe.stopCar();		 }	   }	  );     startAutonomousControlButton.addActionListener(	   new ActionListener()	   {	     public void actionPerformed(ActionEvent ae)		 {		   mapframe.useAutonomousControl();		 }	   }	  );       setLayout(new GridLayout(7,1));	 add(speedGraph);	 add(mergeScoreGraph);	 add(matchRatioGraph);	 add(rotationSpeedGraph);     add(resetMapButton);	 add(eStopButton);	 add(startAutonomousControlButton);  }    public void addMergeScore(double value)  {     mergeScoreGraph.addValue(value);  }    public void addMatchRatio(double value)  {     matchRatioGraph.addValue(value);  }    public void addSpeedValue(double value)  {     speedGraph.addValue(value);  }    public void addRotationSpeedValue(double value)  {     rotationSpeedGraph.addValue(value);  }}